import { Component } from '@angular/core';
import { DbServiceService } from '../services/db-service.service';
import { FormControl, FormGroup } from '@angular/forms';
import { Router } from '@angular/router';

@Component({
  selector: 'app-user-login',
  templateUrl: './user-login.component.html',
  styleUrls: ['./user-login.component.css']
})
export class UserLoginComponent {
  constructor(private help:DbServiceService,private router:Router){}
  loginForm=new FormGroup({
    'email':new FormControl(),
    'password':new FormControl(),
  })
receivedData:any;
  sendSignUpData(){
    console.log(this.loginForm.value);
    this.help.login(this.loginForm.value).subscribe({next:data=>{
      this.receivedData=data;
      console.log(this.receivedData);
      localStorage.setItem('token',this.receivedData.userEmail)
      localStorage.setItem('role',this.receivedData.userRole)
      if(this.receivedData.userRole=="ADMIN"){

      }

    },error:e=>alert(`${e.message}\n${e.status}`)});

  }
}
<<div class="container">
  <div class="card mt-5">

    <div class="card-info">
      <p class="title">Enter Your Details</p>
      <form class="form" [formGroup]="loginForm" (ngSubmit)="sendSignUpData()">
      <input placeholder="Your email" class="input"  type="text" formControlName="email" required>
      <input placeholder="Enter Password" class="input"  type="text" formControlName="password" required>
      <button type="submit" class="btn" href="#">Login</button>
    </form>
    </div>
  </div>
  </div>
