import { Component } from '@angular/core';
import { PageRouterService } from '../services/page-router.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.css']
})
export class HeaderComponent {
constructor(private route:PageRouterService){
  this.checkfun();
}

check:boolean=false;
  logout(){
    localStorage.setItem('token','');
    localStorage.setItem('role','');
    check=false
      this.route.navigateToLoginView();
  }
  checkfun(){
    if(localStorage.getItem('toke'))
  }
}
<p>
  <mat-toolbar>
   <button mat-icon-button class="example-icon" aria-label="Example icon-button with menu icon">
    <img src="../../assets/Nlogo.png" alt="">
  </button>
  <span>ANIME~SITE</span>
  <span class="example-spacer"></span>
  <button mat-icon-button class="example-icon favorite-icon" routerLink="favorite" aria-label="Example icon-button with heart icon">
    <mat-icon>favorite</mat-icon>
  </button>
  <button mat-icon-button class="example-icon" (click)="logout()" aria-label="Example icon-button with share icon">
   <mat-icon>logout</mat-icon>
 </button>
</mat-toolbar>
</p>
