import { HttpClient } from '@angular/common/http';
import { Injectable } from '@angular/core';
import { Observable } from 'rxjs';
import { Photo } from '../model/Photo';

@Injectable({
  providedIn: 'root'
})
export class DbServiceService {

  authAppUrl="lb://authApp/";
  userApp="lb://userApp/";
  photodb="http://localhost:666/photodb/";

  constructor(private httpclient:HttpClient) { }

  getPhoto():Observable<Photo[]>{
    return this.httpclient.get<Photo[]>(this.photodb+"anime/testint4");
  }
  getAllPhoto():Observable<Photo[]>{
    return this.httpclient.get<Photo[]>(this.photodb+"anime/");
  }

  addPhoto(data:object){
      return this.httpclient.post(this.photodb+"addImage",data);
  }

}
