import { Component } from '@angular/core';
import { FormControl, FormGroup } from '@angular/forms';
import { DbServiceService } from '../services/db-service.service';
import { Route, Router } from '@angular/router';

@Component({
  selector: 'app-register',
  templateUrl: './register.component.html',
  styleUrls: ['./register.component.css']
})
export class RegisterComponent {

  constructor(private help:DbServiceService,private router:Router){}
  signupForm=new FormGroup({
    'email':new FormControl(),
    'name':new FormControl(),
    'password':new FormControl(),
  })
receivedData:any;
  sendSignUpData(){
    console.log(this.signupForm.value);
    this.help.register(this.signupForm.value).subscribe({next:data=>{
      this.receivedData=data
    },error:e=>alert(`${e.message}\n${e.status}`)});

  }
}
<div class="container">
<div class="card mt-5">

  <div class="card-info">
    <p class="title">Enter Your Details</p>
    <form class="form" [formGroup]="signupForm" (ngSubmit)="sendSignUpData()">
    <input placeholder="Your Name" class="input"  type="text" formControlName="name" required>
    <input placeholder="Your email" class="input"  type="text" formControlName="email" required>
    <input placeholder="Enter Password" class="input"  type="text" formControlName="password" required>
    <button type="submit" class="btn" href="#">Sign Up</button>
  </form>
  <code routerLink="../login">Already a User LOGIN</code>

  </div>
</div>
</div>
