      'animeName': p.animeName
    };
    this.db.addToUserFavorite(data).subscribe(res => {
      console.log(res);
      let btn;
      btn = document.getElementById(p.animeName);
      btn.style.color = "#ff0000";
      this.snack(p.animeName);
    });
  }
