}
<div class="container">

  <h1>Developer Page</h1>

  <div class="upload">
    <h2>Upload Anime Photo</h2>
    <input type="file" (change)="onFileChange($event)">
    <input type="text" [(ngModel)]="animeName">
    <input type="button" (click)="onUpload()" value="Upload">
  </div>

